const imageInput=document.getElementById("image"),previewImage=document.getElementById("preview_image"),preImageConatiner=document.getElementById("preview-container");imageInput.addEventListener("change",(function(e){const t=imageInput.files[0];t&&(reader=new FileReader,reader.readAsDataURL(t),reader.onload=function(e){preImageConatiner.style.visibility="visible",previewImage.src=e.target.result})}));